var convertData = function(data) { 
	var res = [];
	for(var i = 0; i < data.length; i++) {
		var dataItem = data[i];
		var fromCoord = geoCoordMap[dataItem[0]];
		var toCoord = geoCoordMap[dataItem[1]];

		if(fromCoord && toCoord) {
			res.push([{
				name: dataItem[0],
				coord: fromCoord
			}, {
				name: dataItem[1],
				coord: toCoord
			}]);
		}
	}
	return res;
};

var seriesData=function(routes){

var planePath = 'path://M1705.06,1318.313v-89.254l-319.9-221.799l0.073-208.063c0.521-84.662-26.629-121.796-63.961-121.491c-37.332-0.305-64.482,36.829-63.961,121.491l0.073,208.063l-319.9,221.799v89.254l330.343-157.288l12.238,241.308l-134.449,92.931l0.531,42.034l175.125-42.917l175.125,42.917l0.531-42.034l-134.449-92.931l12.238-241.308L1705.06,1318.313z';

var color = ['#ffa022','#a6c84c', '#46bee9'];
var series = []; 

routes.forEach(function(item, i) {
series.push(
	{
		name: item[0][0] + ' No' + i,
		type: 'lines', 
		coordinateSystem: 'bmap',
		zlevel: 1, 
		effect: { //设置线条动态效果
			show: true,
			period: 6, //周期，也就是速度
			trailLength: 0.7, //痕迹长度
			color: '#fff',
			symbolSize: 3 //痕迹大小
		},
		lineStyle: {
			normal: {
				color: color[i],
				width: 0, //宽度，设置为0为了突出飞机
				curveness: 0.05 //弯曲的弧度
			}
		},
		data: convertData(item)
	},
	//飞机
	{
		name: item[0][0] + ' No' + i,
		type: 'lines',
		coordinateSystem: 'bmap',
		zlevel: 2, //1.设置的层一样，则会使组件或图表样式使用同一个canvas渲染。2.数值越大越在上面
		effect: {
			show: true,
			period: 6,
			trailLength: 0,
			symbol: planePath, //设置图标（也就是飞机），可通过：“image://url”设置为图片
			symbolSize: 12 //设置图标大小
		},
		lineStyle: {
			normal: {
				color: color[i],
				width: 1,
				opacity: 0.5,
				curveness: 0.05
			}
		},
		data: convertData(item)
	}, 
	//设置散点（effectScatter）
	{
		name: item[0][0] + ' No' + i,
		type: 'effectScatter', //类型为动态散点型
		coordinateSystem: 'bmap',
		zlevel: 2,
		//涟漪动画特效
		rippleEffect: {
			brushType: 'stroke' //波纹的绘制方式，还有一种就是“fill(默认)”
		},
		label: {
			normal: { //normal 是图形在默认状态下的样式；emphasis 是图形在高亮状态下的样式。
				show: true,
				position: 'right',
				formatter: '{b}'
			}
		},
		//散点的尺寸：当数据加载完成之后执行回调，通过返回值来设置大小
		symbolSize: function(val) {
			return val[2] / 18;
		},
		itemStyle: {
			normal: {
				color: color[i]
			}
		},
		data: item.map(function(dataItem) {
			var j=i==0?1:0;
			return {
				name: dataItem[j],
				value: geoCoordMap[dataItem[j]].concat([dataItem[2]])
			};
		})
	}
);
});
return series;	
}



